import { useState } from 'react';
import confetti from 'canvas-confetti';
import { Button } from '@/components/ui/button';
import { motion } from 'framer-motion';
import { Sparkles } from 'lucide-react';

export const ConfettiButton = () => {
  const [isPressed, setIsPressed] = useState(false);

  const triggerConfetti = () => {
    setIsPressed(true);

    // Custom confetti with hearts and cat emojis
    const count = 200;
    const defaults = {
      origin: { y: 0.7 },
      zIndex: 9999,
    };

    function fire(particleRatio, opts) {
      confetti({
        ...defaults,
        ...opts,
        particleCount: Math.floor(count * particleRatio),
        spread: 100,
        scalar: 1.2,
        shapes: ['circle', 'square'],
        colors: ['#FFD4DF', '#FFE0ED', '#F1DCCD', '#FF69B4', '#FFB6C1', '#FFC0CB'],
      });
    }

    fire(0.25, {
      spread: 26,
      startVelocity: 55,
    });

    fire(0.2, {
      spread: 60,
    });

    fire(0.35, {
      spread: 100,
      decay: 0.91,
      scalar: 0.8,
    });

    fire(0.1, {
      spread: 120,
      startVelocity: 25,
      decay: 0.92,
      scalar: 1.2,
    });

    fire(0.1, {
      spread: 120,
      startVelocity: 45,
    });

    // Emoji confetti
    const scalar = 2;
    const heart = confetti.shapeFromText({ text: 'â¤ï¸', scalar });
    const cat = confetti.shapeFromText({ text: 'ðŸ±', scalar });
    const yarn = confetti.shapeFromText({ text: 'ðŸ§¶', scalar });

    confetti({
      ...defaults,
      particleCount: 30,
      spread: 120,
      startVelocity: 30,
      shapes: [heart, cat, yarn],
      scalar,
    });

    setTimeout(() => setIsPressed(false), 1000);
  };

  return (
    <motion.div
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
    >
      <Button
        onClick={triggerConfetti}
        disabled={isPressed}
        size="lg"
        className="bg-gradient-to-r from-secondary via-accent to-primary hover:opacity-90 text-foreground font-semibold text-lg px-8 py-6 rounded-full shadow-lg hover:shadow-xl transition-all duration-300 border-2 border-white/50"
      >
        <Sparkles className="w-5 h-5 mr-2" />
        {isPressed ? 'âœ¨ Showering Love! âœ¨' : 'ðŸ’– Press Me! ðŸ’–'}
        <Sparkles className="w-5 h-5 ml-2" />
      </Button>
    </motion.div>
  );
};
